<template>
  <a v-bind:href="dynamicLink">Go to Vue.js</a>
  <img v-bind:src="imageSrc" alt="Dynamic Image">
  <div v-bind:style="dynamicStyle">This is a styled div</div>
  <div :style="`color: ${textColor}; font-size: ${textSize}px`">Dynamic Styled Text</div>
  <div v-bind:class="{ active: isActive, 'text-danger': hasError }">Dynamic Class Binding</div>
  <div v-bind:class="[activeClass, errorClass]">Dynamic Class with Array</div>
  <img :src="imageSrc" :alt="imageAlt">
</template>

<script>
export default {
  data() {
    return {
      dynamicLink: 'https://vuejs.org',
      imageSrc: 'data:image/png;base64,',
      dynamicStyle: {
        color: 'red',
        fontSize: '20px'
      },
      textColor: 'blue',
      textSize: 18,
      isActive: false,
      hasError: true,
       activeClass: 'active',
      errorClass: 'text-danger'
    }
  }
}
</script>
